package JavaBasics;

public class InPostfix {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 3;
		int y;
		System.out.println("value " + x);
		y = x++;
		System.out.println("value " + y);
		System.out.println("value " + x);

		y = x++;
		System.out.println("value " + y);
		System.out.println("value " + x);

		y = x++;
		System.out.println("value " + y);
		System.out.println("value " + x);

	}

}
